-- Create State Teams Script (Simple Version)
-- Run this in Supabase SQL editor to create all US state teams
-- If teams already exist, you may get duplicate errors - that's okay

-- Temporarily disable RLS for teams table
ALTER TABLE teams DISABLE ROW LEVEL SECURITY;

-- Clear existing state teams (optional - uncomment if you want to recreate)
-- DELETE FROM teams WHERE badges @> ARRAY['state_team'];

-- Insert all 50 US state teams

-- Re-enable RLS for teams table
ALTER TABLE teams ENABLE ROW LEVEL SECURITY;

-- Show created teams
SELECT name, category, privacy, max_members, created_at 
FROM teams 
WHERE badges @> ARRAY['state_team'] 
ORDER BY name;








